import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import './Compose.css';

class ComposePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      to: '',
      subject: '',
      body: '',
      selectedFile: null,
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleFileChange = (e) => {
    this.setState({ selectedFile: e.target.files[0] });
  };
  
  handleSubmit = (e) => {
    e.preventDefault();
    const { to, subject, body, selectedFile } = this.state;
  
    const formData = new FormData();
    formData.append('invoice', selectedFile); // Attach the file with key 'invoice'
  
    // Prepare the rest of the data as a JSON string
    const emailData = JSON.stringify({ to, subject, body });
    formData.append('data', emailData); // Append the JSON string with key 'data'
  
    fetch(`${process.env.REACT_APP_API_URL}/send-invoice`, {
      method: 'POST',
      body: formData,
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json(); // Only parse as JSON if response is OK
    })
    .then(data => {
      console.log('Success:', data);
      alert("Invoice sent successfully!");
      // Handle success
    })
    .catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
    });
  };
  
  render() {
    const { to, subject, body } = this.state;
    return (
      <div className="compose-container">
        <nav className="sidebar">
          <h1>Welcome!</h1>
          <NavLink className="nav-link" activeClassName="active-link" to="/home">Home</NavLink>
          <NavLink className="nav-link" activeClassName="active-link" to="/compose">Compose</NavLink>
          <NavLink className="nav-link" activeClassName="active-link" to="/inbox">Inbox</NavLink>
          <NavLink className="nav-link" activeClassName="active-link" to="/sent">Sent</NavLink>
        </nav>
        <div className="compose">
          <form onSubmit={this.handleSubmit}>
            <label htmlFor="to">To:</label>
            <input
              type="email"
              id="to"
              name="to"
              className="input-box"
              value={to}
              onChange={this.handleInputChange}
              placeholder="Recipient's email"
            />
            <label htmlFor="subject">Subject:</label>
            <input
              type="text"
              id="subject"
              name="subject"
              className="input-box"
              value={subject}
              onChange={this.handleInputChange}
              placeholder="Subject"
            />
            <label htmlFor="body">Body:</label>
            <textarea
              name="body"
              className="input-box"
              value={body}
              onChange={this.handleInputChange}
              placeholder="Your message"
            ></textarea>
            <input
              type="file"
              onChange={this.handleFileChange}
              className="attach-btn"
            />
            <button type="submit" className="sentpage-compose">
              Send
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default ComposePage;
