import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';

class ComposePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      to: '',
      subject: '',
      body: '',
      selectedFile: null,
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleFileChange = (e) => {
    this.setState({ selectedFile: e.target.files[0] });
  };
  handleSubmit = (e) => {
    e.preventDefault();
    const { to, subject, body, selectedFile } = this.state;
  
    const formData = new FormData();
    formData.append('invoice', selectedFile); // Attach the file with key 'invoice'
  
    // Prepare the rest of the data as a JSON string
    const emailData = JSON.stringify({ to, subject, body });
    formData.append('data', emailData); // Append the JSON string with key 'data'
  
    fetch(`${process.env.REACT_APP_API_URL}/send-invoice`, {
      method: 'POST',
      body: formData,
    })
    .then(response => {
      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }
      return response.json(); // Only parse as JSON if response is OK
    })
    .then(data => {
      console.log('Success:', data);
      alert("Invoice sent successfully!");
      // Handle success
    })
    .catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
    });
  };
  

  render() {
    const { to, subject, body } = this.state;
    return (
      <div className="compose-container">
        <nav>
          <h1>Welcome!</h1>
          <NavLink to="/home">Home</NavLink>
          <NavLink to="/compose">Compose</NavLink>
          <NavLink to="/inbox">Inbox</NavLink>
          <NavLink to="/sent">Sent</NavLink>
        </nav>
        <form onSubmit={this.handleSubmit}>
          <input
            type="email"
            id="to"
            name="to"
            value={to}
            onChange={this.handleInputChange}
            placeholder="Recipient's email"
          />
          <input
            type="text"
            id="subject"
            name="subject"
            value={subject}
            onChange={this.handleInputChange}
            placeholder="Subject"
          />
          <textarea
            name="body"
            value={body}
            onChange={this.handleInputChange}
            placeholder="Your message"
          ></textarea>
          <input
            type="file"
            onChange={this.handleFileChange}
          />
          <button type="submit">
            Send
          </button>
        </form>
      </div>
    );
  }
}

export default ComposePage;
