import React, { useState } from 'react';
import './Signup.css'; // Make sure this points to your CSS file

function Signup() {
  const [Name, setName] = useState(''); // Changed variable name to Name
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ Name, email, password }), // Changed variable name to Name
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message);
      }

      // Registration successful, redirect or display a success message
    } catch (error) {
      console.error('Registration failed:', error.message);
      setError(error.message); // Set error state based on the error message from the backend
    }
  };

  return (
    <div className="signup-container">
      {/* Background image half */}
      <div className="background-half"></div>

      {/* Signup form half */}
      <div className="form-half">
        <div className="signup-form">
          <h2>Sign Up</h2>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="text"
                placeholder="Name" // Changed placeholder to Name
                className="input-field"
                value={Name} // Changed variable name to Name
                onChange={(e) => setName(e.target.value)} // Changed variable name to Name
              />
            </div>
            <div className="form-group">
              <input
                type="email"
                placeholder="Email Address"
                className="input-field"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                placeholder="Create Password"
                className="input-field"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            {error && <div className="error-message">{error}</div>}
            <button type="submit" className="signup-button">
              Create an account
            </button>
          </form>
          <div className="login-section">
            <span>
              Already have an account? <a href="/" className="login-link">Log in</a>
            </span>
          </div>
          <div className="social-signup">
            {/*will update with FontAwesome */}
            <button className="social-button google">G</button>
            <button className="social-button facebook">f</button>
            <button className="social-button instagram">In</button>
            <button className="social-button twitter">T</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Signup;
