import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import './Compose.css';

class ComposePage extends Component {
  // Constructor and state setup remains unchanged

  // handleInputChange and handleFileChange methods remain unchanged

  handleSubmit = (e) => {
    // Your handleSubmit method remains unchanged
  };
  
  render() {
    const { to, subject, body } = this.state;
    return (
      <div className="compose-container">
        <nav className="sidebar">
          <h1>Welcome!</h1>
          <NavLink className="nav-link" to="/home">Home</NavLink>
          <NavLink className="nav-link" to="/compose">Compose</NavLink>
          <NavLink className="nav-link" to="/inbox">Inbox</NavLink>
          <NavLink className="nav-link" to="/sent">Sent</NavLink>
        </nav>
        <div className="compose">
          <form onSubmit={this.handleSubmit}>
            <div className="input-box">
              <input
                type="email"
                id="to"
                name="to"
                className="to-box"
                value={to}
                onChange={this.handleInputChange}
                placeholder="Recipient's email"
              />
            </div>
            <div className="input-box">
              <input
                type="text"
                id="subject"
                name="subject"
                className="subject-box"
                value={subject}
                onChange={this.handleInputChange}
                placeholder="Subject"
              />
            </div>
            <div className="input-box">
              <textarea
                name="body"
                className="body-box"
                value={body}
                onChange={this.handleInputChange}
                placeholder="Your message"
              ></textarea>
            </div>
            <div className="input-box">
              <input
                type="file"
                onChange={this.handleFileChange}
                className="attach-btn"
              />
            </div>
            <button type="submit" className="compose input[type='submit']">
              Send
            </button>
          </form>
        </div>
      </div>
    );
  }
}

export default ComposePage;
