import React, { Component } from 'react';
import './Inbox.css';
import { Link } from 'react-router-dom';

class InboxPage extends Component {
    constructor(props) {
        super(props);
        this.state = {
            inboxItems: [],
        };
    }

    componentDidMount() {
        this.fetchInboxItems();
    }

    fetchInboxItems = () => {
        fetch('http://localhost:3000/inbox') // Adjust the URL as needed
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
                throw new Error('Network response was not ok.');
            })
            .then(data => this.setState({ inboxItems: data }))
            .catch(error => console.error('Error fetching inbox items:', error));
    };

    render() {
        const { inboxItems } = this.state;
        return (
            <div className="inbox-container">
                <aside className="menu-sidebar">
                    <Link to="/" className="menu-item">Home</Link>
                    <Link to="/compose" className="menu-item">Compose</Link>
                    <Link to="/inbox" className="menu-item active">Inbox</Link>
                    <Link to="/sent" className="menu-item">Sent</Link>
                </aside>
                <main className="inbox-content">
                    <header className="welcome-header">
                        <h1>Inbox</h1>
                        <div className="search-bar">
                            <input type="text" placeholder="Search file" />
                        </div>
                    </header>
                    <section className="inbox-list">
                        {inboxItems.map((item) => (
                            <Link to={`/inbox/${item.id}`} key={item.id} className="inbox-item">
                                <div className="sender">{item.sender}</div>
                                <div className="subject">{item.subject} - {item.summary}</div>
                            </Link>
                        ))}
                    </section>
                </main>
            </div>
        );
    }
}

export default InboxPage;
