import React, { useState, useEffect } from 'react';
import './Inbox.css';

const InboxPage = () => {
  const [emails, setEmails] = useState([]);

  useEffect(() => {
    // Simulate fetching data from storage
    const storedEmails = [
      { sender: 'Hakim', subject: 'Construction E-invoice', content: 'This is your e-invoice for the construction' },
      { sender: 'Aanshu', subject: 'Plumbing E-invoice', content: 'This is your e-invoice for the plumbing' },
      { sender: 'Jack', subject: 'Groceries E-invoice', content: 'This is your e-invoice for the groceries' },
      { sender: 'Nabihah', subject: 'Groceries E-invoice', content: 'This is your e-invoice for the groceries' },
      { sender: 'Aanshu', subject: 'Construction E-invoice', content: 'This is your e-invoice for the construction' }
    ];
    
    // Assuming the data is stored in local storage or similar
    // You would replace the above with a real fetch from your storage system
    setEmails(storedEmails);
  }, []);

  return (
    <div className="page-container">
      <header className="header">
        <h1>Welcome!</h1>
        <span>"User's Name"</span>
      </header>
      <aside className="menu">
        <ul>
          <li>Home</li>
          <li>Compose</li>
          <li>Inbox</li>
          <li>Sent</li>
        </ul>
      </aside>
      <section className="content">
        <input type="search" placeholder="Search file" />
        <ul>
          {emails.length > 0 ? (
            emails.map((email, index) => (
              <li key={index}>
                <strong>{email.sender}</strong> {email.subject} - {email.content}
              </li>
            ))
          ) : (
            <li>No emails found</li>
          )}
        </ul>
      </section>
    </div>
  );
};

export default InboxPage;
