import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import './Compose.css';

class ComposePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      to: '',
      subject: '',
      body: '',
      selectedFile: null,
      fileName: '', // To display the name of the attached file
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleFileChange = (e) => {
    // Set both the file and its name in state for display
    const file = e.target.files[0];
    this.setState({ 
      selectedFile: file,
      fileName: file ? file.name : '', // Update the fileName state
    });
  };

  handleAttachClick = () => {
    this.fileInput.click();
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { to, subject, body, selectedFile } = this.state;
  
    const formData = new FormData();
    formData.append('to', to);
    formData.append('subject', subject);
    formData.append('body', body);
    formData.append('file', selectedFile);
  
    fetch('http://localhost:3000/sendFileInternally', {
      method: 'POST',
      body: formData,
    }).then(response => {
      if(response.ok) {
        return response.json();
      }
      throw new Error('Network response was not ok.');
    }).then(data => {
      console.log(data.message);
      alert("Message sent successfully!"); // Simple feedback
      // Clear the form after successful submission
      this.setState({
        to: '',
        subject: '',
        body: '',
        selectedFile: null,
        fileName: '',
      });
      // Optionally, redirect or fetch to update the inbox view
    }).catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
    });
  };  

  render() {
    const { to, subject, body, fileName } = this.state;

    return (
      <div className="compose-container">
        <aside className="sidebar">
          <nav>
            <h1>Welcome!</h1>
            <NavLink to="/home" exact className="nav-link" activeClassName="active-link">Home</NavLink>
            <NavLink to="/compose" className="nav-link" activeClassName="active-link">Compose</NavLink>
            <NavLink to="/inbox" className="nav-link" activeClassName="active-link">Inbox</NavLink>
            <NavLink to="/sent" className="nav-link" activeClassName="active-link">Sent</NavLink>
          </nav>
        </aside>
        <main className="compose">
          <form onSubmit={this.handleSubmit}>
            <div className='input-box'>
              <div className="to-box">
                <label htmlFor="to">To:</label>
                <input type="email" id="to" name="to" value={to} onChange={this.handleInputChange} />
              </div>

              <div className="subject-box">
                <label htmlFor="subject">Subject:</label>
                <input type="text" id="subject" name="subject" value={subject} onChange={this.handleInputChange} />
              </div>

              <textarea placeholder="Write your text here..." name="body" value={body} onChange={this.handleInputChange}></textarea>

              <input type="file" id="fileInput" style={{ display: 'none' }} onChange={this.handleFileChange} ref={input => this.fileInput = input} />
              <button type="button" className="attach-btn" onClick={this.handleAttachClick}>
                Attach file
              </button>
              {fileName && <div className="file-name">{fileName}</div>} {/* Display the attached file name */}
            </div>
            <input type="submit" value="Send" />
          </form>
        </main>
      </div>
    );
  }
}

export default ComposePage;
