// InboxPage.js

import React from 'react';
import './Inbox.css';
import { Link } from 'react-router-dom';

const inboxItems = [
  {
    id: 1,
    sender: 'Hakiem',
    subject: 'Construction E-Invoice',
    summary: 'This is your e-invoice for the construction',
  },
  // ... more items
];

const InboxPage = () => {
  return (
    <div className="inbox-container">
      <aside className="menu-sidebar">
        {/* Menu Sidebar Items */}
        <Link to="/" className="menu-item">Home</Link>
        <Link to="/compose" className="menu-item">Compose</Link>
        <Link to="/inbox" className="menu-item active">Inbox</Link>
        <Link to="/sent" className="menu-item">Sent</Link>
      </aside>
      <main className="inbox-content">
        <header className="welcome-header">
          <h1>Inbox</h1>
          <div className="search-bar">
            <input type="text" placeholder="Search file" />
          </div>
        </header>
        <section className="inbox-list">
          {inboxItems.map((item) => (
            <Link to={`/inbox/${item.id}`} key={item.id} className="inbox-item">
              <div className="sender">{item.sender}</div>
              <div className="subject">{item.subject} - {item.summary}</div>
            </Link>
          ))}
        </section>
      </main>
    </div>
  );
};

export default InboxPage;
