import React, { useState } from 'react';
import './Login.css'; // Make sure this points to your CSS file

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message);
      }

      // Redirect to home page after successful login
      window.location.href = '/home';
    } catch (error) {
      console.error('Login failed:', error.message);
      setError(error.message);
    }
  };

  return (
    <div className="login-container">
      {/* Background image half */}
      <div className="background-half"></div>

      {/* Login form half */}
      <div className="form-half">
        <div className="login-form">
          <h2>Login</h2>
          <div className="login-info">Login to your account in seconds</div>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <input
                type="email"
                placeholder="Email Address"
                className="input-field"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
            </div>
            <div className="form-group">
              <input
                type="password"
                placeholder="Password"
                className="input-field"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            {error && <div className="error-message">{error}</div>}
            <button type="submit" className="login-button">
              Log in
            </button>
          </form>
          <div className="signup-section">
            <span>
              Don't have an account? <a href="/signup" className="sign-up-link">Sign up</a>
            </span>
          </div>
          <div className="social-login">
            {/*will update with FontAwesome */}
            <button className="social-button google">G</button>
            <button className="social-button facebook">f</button>
            <button className="social-button instagram">In</button>
            <button className="social-button twitter">T</button>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Login;
