// ComposePage.js
import React, { Component } from 'react';
import { NavLink } from 'react-router-dom';
import './Compose.css';

class ComposePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      to: '',
      subject: '',
      body: '',
      selectedFile: null
    };
  }

  handleInputChange = (e) => {
    const { name, value } = e.target;
    this.setState({ [name]: value });
  };

  handleFileChange = (e) => {
    this.setState({ selectedFile: e.target.files[0] });
  };

  handleAttachClick = () => {
    this.fileInput.click();
  };

  handleSubmit = (e) => {
    e.preventDefault();
    const { to, subject, body, selectedFile } = this.state;
  
    // Create FormData object to send the file and other data
    const formData = new FormData();
    formData.append('to', to);
    formData.append('subject', subject);
    formData.append('body', body);
    formData.append('file', selectedFile); // Assuming 'file' is the field expected by your backend
  
    // API request to send the invoice
    fetch('http://localhost:3000/sendFileInternally', { // Adjust the URL as needed
      method: 'POST',
      body: formData,
      // Don't set 'Content-Type': 'multipart/form-data' header manually,
      // as the browser will add it automatically, including the boundary parameter
    }).then(response => {
      if(response.ok) {
        return response.json();
      }
      throw new Error('Network response was not ok.');
    }).then(data => {
      console.log(data.message);
      // Handle success, maybe clear the form or redirect the user
    }).catch(error => {
      console.error('There has been a problem with your fetch operation:', error);
    });
  };  

  render() {
    const { to, subject, body } = this.state;

    return (
      <div className="compose-container">
        <aside className="sidebar">
          <nav>
            <h1>Welcome!</h1>
            <NavLink to="/home" exact className="nav-link" activeClassName="active-link">Home</NavLink>
            <NavLink to="/compose" className="nav-link" activeClassName="active-link">Compose</NavLink>
            <NavLink to="/inbox" className="nav-link" activeClassName="active-link">Inbox</NavLink>
            <NavLink to="/sent" className="nav-link" activeClassName="active-link">Sent</NavLink>
          </nav>
        </aside>
        <main className="compose">
          <form onSubmit={this.handleSubmit}>
            <div className='input-box'>

              <div className="to-box">
                <label htmlFor="to">To:</label>
                <input
                  type="email"
                  id="to"
                  name="to"
                  value={to}
                  onChange={this.handleInputChange}
                />
              </div>

              <div className="subject-box">
                <label htmlFor="subject">Subject:</label>
                <input
                  type="text"
                  id="subject"
                  name="subject"
                  value={subject}
                  onChange={this.handleInputChange}
                />
              </div>

              <textarea
                placeholder="Write your text here..."
                name="body"
                value={body}
                onChange={this.handleInputChange}
              ></textarea>

              <input
                type="file"
                id="fileInput"
                style={{ display: 'none' }}
                onChange={this.handleFileChange}
                ref={input => this.fileInput = input}
              />
              <button
                type="button"
                className="attach-btn"
                onClick={this.handleAttachClick}
              >
                Attach file
              </button>
            </div>
            <input type="submit" value="Send" />
          </form>
        </main>
      </div>
    );
  }
}

export default ComposePage;
